hudson.tasks.Shell.command:
set -x
if [[ ! -d $OUTPUT_LOCATION ]]; then mkdir $OUTPUT_LOCATION || true; fi

vol.py ${VOLATILITY_ARGS} mac_ifconfig >${OUTPUT_LOCATION}/mac_ifconfig_output;
vol.py ${VOLATILITY_ARGS} mac_netstat >${OUTPUT_LOCATION}/mac_netstat_output;
vol.py ${VOLATILITY_ARGS} mac_network_conns >${OUTPUT_LOCATION}/mac_network_conns_output;
vol.py ${VOLATILITY_ARGS} mac_dead_sockets >${OUTPUT_LOCATION}/mac_dead_sockets_output;
vol.py ${VOLATILITY_ARGS} mac_route >${OUTPUT_LOCATION}/mac_route_output;
vol.py ${VOLATILITY_ARGS} mac_arp >${OUTPUT_LOCATION}/mac_arp_output;
## ipfw to pf?
vol.py ${VOLATILITY_ARGS} mac_ip_filter >${OUTPUT_LOCATION}/mac_ip_filter_output;
